INSERT INTO pages (slug, title, html_content, visibility, created_at) VALUES
('home', 'Welcome to Goldwing Association', '<p>Goldwing Association is the national home for riders, chapters, and the open road.</p>', 'public', NOW()),
('about', 'About the Association', '<p>Goldwing Association brings together riders across Australia and New Zealand for safe, social, and well planned rides.</p><div class="grid grid-3"><div class="card"><h3>National Committee</h3><p>Meet the volunteers who guide the association and keep it moving.</p><p><a class="button" href="/?page=committee">View committee</a></p></div><div class="card"><h3>Chapters and Representatives</h3><p>Find your local chapter and connect with regional contacts.</p><p><a class="button" href="/?page=chapters-representatives">View chapters</a></p></div><div class="card"><h3>Constitution</h3><p>Read the rules and governance framework that shape the association.</p><p><a class="button" href="/?page=constitution">View constitution</a></p></div><div class="card"><h3>Presidents Address</h3><p>A welcome message from the current National President.</p><p><a class="button" href="/?page=presidents-address">Read the address</a></p></div><div class="card"><h3>Annual General Meeting</h3><p>Learn about the AGM weekend and key member events.</p><p><a class="button" href="/?page=agm">AGM details</a></p></div></div><div class="card"><img src="/uploads/about/about-chapters-hero.jpg" alt="Goldwing riders" style="width:100%; border-radius:8px; margin-bottom:1rem;"><h3>Rides, chapters, and support</h3><p>Members ride together, support each other on the road, and build lasting friendships.</p></div>', 'public', NOW()),
('ride-calendar', 'Ride Calendar', '<p>Check back for the latest rides and chapter meetups.</p>', 'public', NOW()),
('membership', 'Membership Selection', '<p>Choose from Full, Associate, or Life membership options.</p><p><a href="/apply.php">Apply for membership</a></p>', 'public', NOW()),
('events', 'Events', '<p>Our events calendar is updated regularly with rallies and rides.</p>', 'public', NOW()),
('committee', 'National Committee', '<p>Riding with a powerful motorcycle comes a skilled crew ready to navigate the roads here at the Australian Goldwing Association.</p><div class="grid grid-3 committee-grid"><div class="card"><img src="/uploads/about/committee-lewis-furner.webp" alt="Lewis Furner" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Lewis Furner</h3><p>National President</p><p>Phone: 0429 324 426</p><p><a href="mailto:aga.president@goldwing.org.au">aga.president@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Paul" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Paul</h3><p>National Vice President</p><p>Phone: TBC</p><p><a href="mailto:aga.vicepresident@goldwing.org.au">aga.vicepresident@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-peter-macpherson.webp" alt="Peter Macpherson" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Peter Macpherson</h3><p>National Secretary</p><p>Phone: TBC</p><p><a href="mailto:aga.secretary@goldwing.org.au">aga.secretary@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-robyn-furner.webp" alt="Robyn Furner" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Robyn Furner</h3><p>National Treasurer</p><p>Phone: 0412 662 448</p><p><a href="mailto:aga.treasurer@goldwing.org.au">aga.treasurer@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-wayne-gannon.webp" alt="Wayne Gannon" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Wayne Gannon</h3><p>National Promotions Officer</p><p>Phone: 0449 150 530</p><p><a href="mailto:aga.promotions@goldwing.org.au">aga.promotions@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-les-sorensen.webp" alt="Les Sorensen" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Les Sorensen</h3><p>Committee Member</p><p>Phone: 0412 226 886</p><p><a href="mailto:aga.committee1@goldwing.org.au">aga.committee1@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-david-goodchild.webp" alt="David Goodchild" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Paul David Goodchild</h3><p>Committee Member</p><p>Phone: TBC</p><p><a href="mailto:aga.committee2@goldwing.org.au">aga.committee2@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-kevin-lane.webp" alt="Kevin Lane" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Kevin Lane</h3><p>Committee Member</p><p>Phone: TBC</p><p><a href="mailto:aga.committee3@goldwing.org.au">aga.committee3@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Mal Allen" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Mal Allen</h3><p>Public Officer</p><p>Phone: 0455 380 162</p><p><a href="mailto:aga.publicofficer@goldwing.org.au">aga.publicofficer@goldwing.org.au</a></p></div></div>', 'public', NOW()),
('chapters-representatives', 'Chapters and Area Representatives', '<p>Local chapters are the heart of the association. Each state has representatives who coordinate rides and support members.</p><div class="card"><img src="/uploads/about/about-chapters-hero.jpg" alt="Goldwing riders" style="width:100%; border-radius:8px; margin-bottom:1rem;"><h3>Many hands make light work</h3><p>Join your local chapter to meet riders, plan chapter rides, and stay connected.</p></div><div class="grid grid-3 chapter-state-grid"><div class="card chapter-state-card"><img src="/uploads/library/2025/state-nsw.svg" alt="New South Wales"><h3><a href="/?page=chapters-nsw">New South Wales</a></h3><ul class="chapter-list"><li><a href="/?page=chapters-nsw">Central Coast Chapter</a></li><li><a href="/?page=chapters-nsw">Central West Chapter</a></li><li><a href="/?page=chapters-nsw">Coffs Coast Chapter</a></li><li><a href="/?page=chapters-nsw">New England Chapter</a></li><li><a href="/?page=chapters-nsw">North West Chapter</a></li><li><a href="/?page=chapters-nsw">Riverina Chapter</a></li><li><a href="/?page=chapters-nsw">Sydney Chapter</a></li></ul></div><div class="card chapter-state-card"><img src="/uploads/library/2025/state-qld.svg" alt="Queensland"><h3><a href="/?page=chapters-qld">Queensland</a></h3><ul class="chapter-list"><li><a href="/?page=chapters-qld">Brisbane Chapter</a></li><li><a href="/?page=chapters-qld">Fraser Coast Chapter</a></li></ul></div><div class="card chapter-state-card"><img src="/uploads/library/2025/state-wa.svg" alt="Western Australia"><h3><a href="/?page=chapters-wa">Western Australia</a></h3><ul class="chapter-list"><li><a href="/?page=chapters-wa">Perth Chapter</a></li><li><a href="/?page=chapters-wa">West Coast Wings Chapter</a></li></ul></div><div class="card chapter-state-card"><img src="/uploads/library/2025/state-sa.svg" alt="South Australia"><h3><a href="/?page=chapters-sa">South Australia</a></h3><ul class="chapter-list"><li><a href="/?page=chapters-sa">South Australian Chapter</a></li></ul></div><div class="card chapter-state-card"><img src="/uploads/library/2025/state-tas.svg" alt="Tasmania"><h3><a href="/?page=chapters-tas">Tasmania</a></h3><ul class="chapter-list"><li><a href="/?page=chapters-tas">Tasmania Chapter</a></li></ul></div><div class="card chapter-state-card"><img src="/uploads/library/2025/state-vic.svg" alt="Victoria"><h3><a href="/?page=chapters-vic">Victoria</a></h3><p>No local chapter yet. Contact the National President.</p></div><div class="card chapter-state-card"><img src="/uploads/library/2025/state-nt.svg" alt="Northern Territory"><h3><a href="/?page=chapters-nt">Northern Territory</a></h3><p>No local chapter yet. Contact the National President.</p></div></div><p>If you are new, start by joining a chapter and introducing yourself at a ride.</p>', 'public', NOW()),
('chapters-nsw', 'New South Wales Chapters', '<p>Local chapters across New South Wales coordinate rides and support members throughout the region.</p><div class="card"><img src="/uploads/about/about-chapters-hero.jpg" alt="New South Wales riders" style="width:100%; border-radius:8px; margin-bottom:1rem;"><h3>New South Wales</h3><p>Chapter representatives can help you connect with local rides and events.</p></div><div class="grid grid-3 chapter-grid"><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Mal Allen" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Central Coast Chapter</h3><p>Mal Allen</p><p><a href="tel:0455380162">0455 380 162</a></p><p><a href="mailto:ar.centralcoast@goldwing.org.au">ar.centralcoast@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Dorothy Springett" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Central West Chapter</h3><p>Dorothy Springett</p><p><a href="tel:0402075741">0402 075 741</a></p><p><a href="mailto:ar.centralwest@goldwing.org.au">ar.centralwest@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Brian Platts" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>Coffs Coast Chapter</h3><p>Brian Platts</p><p><a href="tel:0400409681">0400 409 681</a></p><p><a href="mailto:ar.coffscoast@goldwing.org.au">ar.coffscoast@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Allan Piddington" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>New England Chapter</h3><p>Allan Piddington</p><p><a href="tel:0267722706">02 6772 2706</a></p><p><a href="mailto:ar.newengland@goldwing.org.au">ar.newengland@goldwing.org.au</a></p></div><div class="card"><img src="/uploads/about/committee-placeholder.png" alt="Stephen (Skippy) Ward" style="width:100%; border-radius:8px; margin-bottom:0.75rem;"><h3>North